values.policy:
{
  "Version": "2012-10-17",
  "Statement": [
    {
      "Sid": "Allow access for all principals in the account that are authorized",
      "Effect": "Allow",
      "Action": [
        "kms:ReEncrypt*",
        "kms:GenerateDataKey*",
        "kms:Encrypt",
        "kms:DescribeKey",
        "kms:Decrypt",
        "kms:CreateGrant"
      ],
      "Resource": "*",
      "Principal": {
        "AWS": "arn:aws:iam::027500179340:root"
      },
      "Condition": {
        "StringEquals": {
          "kms:CallerAccount": "027500179340",
          "kms:ViaService": "eks.ap-northeast-2.amazonaws.com"
        }
      }
    },
    {
      "Sid": "Allow direct access to key metadata to the account",
      "Effect": "Allow",
      "Action": [
        "kms:RevokeGrant",
        "kms:List*",
        "kms:Get*",
        "kms:Describe*"
      ],
      "Resource": "*",
      "Principal": {
        "AWS": "arn:aws:iam::027500179340:root"
      }
    },
    {
      "Sid": "Allow access for Key Administrators",
      "Effect": "Allow",
      "Action": "kms:*",
      "Resource": "*",
      "Principal": {
        "AWS": "arn:aws:iam::027500179340:role/eks-blueprints-for-terraform-workshop-admin"
      }
    },
    {
      "Sid": "Allow use of the key",
      "Effect": "Allow",
      "Action": [
        "kms:ReEncrypt*",
        "kms:GenerateDataKey*",
        "kms:Encrypt",
        "kms:DescribeKey",
        "kms:Decrypt"
      ],
      "Resource": "*",
      "Principal": {
        "AWS": "arn:aws:iam::027500179340:role/infrastructure-cluster-role"
      }
    },
    {
      "Sid": "Allow attachment of persistent resources",
      "Effect": "Allow",
      "Action": [
        "kms:RevokeGrant",
        "kms:ListGrants",
        "kms:CreateGrant"
      ],
      "Resource": "*",
      "Principal": {
        "AWS": "arn:aws:iam::027500179340:role/infrastructure-cluster-role"
      },
      "Condition": {
        "Bool": {
          "kms:GrantIsForAWSResource": "true"
        }
      }
    }
  ]
}

